import { Client } from '@opensearch-project/opensearch';

export const client = new Client({
  node: 'https://search-amazon-opensearch-hnkbsyqioeqlcw4hwvtmb633ba.ap-northeast-2.es.amazonaws.com',
  ssl: {
    rejectUnauthorized: false // 개발 환경에서만 사용. 프로덕션에서는 제거해야 합니다.
  },
  auth: {
    username: 'root', // Amazon OpenSearch Service의 사용자 이름
    password: '1234qwerA!'  // Amazon OpenSearch Service의 비밀번호
  }
});
