// src/views/SearchMain.vue

<template>
    <div class="wrap">
        <header class="header">
            <div class="headerName">Open<br>Search</div>
            <div class="search-container">
                <input type="search" placeholder="검색어 입력" v-model="searchQuery" @input="fetchSearchResults">
            </div>
        </header>
        <div class="content">
            <div class="context">
                검색어 : {{ searchQuery }}
                <div v-if="results.length">
                    <p>검색 결과 :</p>
                    <ul>
                        <li v-for="result in results" :key="result.link">{{ result.title }}</li>
                    </ul>
                </div>
                <div v-else>
                    <p>결과가 없습니다.</p>
                </div>
            </div>
        </div>
    </div>
</template>

<script>
import axios from 'axios';

export default {
    name: "SearchMain",
    data() {
        return {
            searchQuery: '',
            results: []
        };
    },
    methods: {
        async fetchSearchResults() {
            const query = this.searchQuery.trim();
            if (query.length > 0) {
                try {
                    const response = await axios.get(`/api/search/${query}`);
                    this.results = response.data.items;
                } catch (error) {
                    console.error('Error fetching search results:', error);
                    this.results = [];
                }
            } else {
                this.results = [];
            }
        }
    }
};
</script>

<style scoped>
.header {
    padding: 20px;
    background-color: #f8f9fa;
}

.search-container {
    position: relative;
    width: 200px;
}

.search-container input {
    width: 100%;
    padding: 10px 25px 10px 10px;
    border: 1px solid #ccc;
    border-radius: 4px;
}

.content {
    padding: 20px;
    background-color: #ffffff;
}

.context {
    margin-top: 10px;
}
</style>
